    $('#userAddTitleForm').on('submit', function (e) {
        e.preventDefault();
        const form = $(this), list = $('.title-list');
        const title_value = form.find("[name='name']").val();
        const title_text = form.find("[name='name'] option:selected").text();
        const description = form.find("[name='description']").val();
        const accept_order = form.find("[name='accept_order']").is(":checked") ? 1 : 0;
        const order_price = form.find("[name='order_price']").val();
        let hasError = false;

        if (form.hasClass('is-edit')) {
            form.removeClass('is-edit');
            $.ajax({
                method: 'PATCH',
                url: 'ajax/artists/titles.php',
                data: {
                    title_id: title_value,
                    description: description,
                    accept_order: accept_order,
                    order_price: order_price
                },
                success: function (response) {
                    const $item = $('.title-list-item.has-focus');
                    $item.removeClass('has-focus');
                    response = JSON.parse(response);
                    if (response.accept_order == 1) {
                        $item.find('.order_price').addClass('accepts');
                        $item.find('.order_price').text(formatPrice(response.order_price ?? ''));
                    } else {
                        $item.removeClass('accepts');
                        $item.find('.order_price').removeClass('accepts');
                        $item.find('.order_price').text('');
                    }
                    $item.find('.order_price').data('price', response.order_price);
                    $item.find('.description').text(response.description ?? '');
                    list.find('.alert-danger').remove();
                    form.collapse('hide');
                },
                error: function (error) {
                    console.log("error : " + JSON.stringify(error));
                }
            });
        } else {
            $.ajax({
                method: 'POST',
                url: 'ajax/artists/titles.php',
                data: {
                    title_id: title_value,
                    title_text: title_text,
                    description: description,
                    accept_order: accept_order,
                    order_price: order_price
                },
                success: function (response) {
                    list.find('.alert').remove();
                    list.prepend(response);
                    if (list.find('.alert').length === 0) {
                        form.collapse('hide');
                    }
                    list.find('.list-empty').addClass('d-none');
                },
                error: function (error) {
                    console.log("error : " + JSON.stringify(error));
                }
            });
        }
    });

