    $('#userAddExperienceForm').on('submit', function (e) {
        e.preventDefault();
        const form = $(this), list = $('.portfolio-list');
        const name = form.find("[name='name']").val();
        const date = form.find("[name='date']").val();
        const url = form.find("[name='url']").val();
        const description = form.find("[name='description']").val();

        let formData = new FormData();
        // formData.append("is_edit", form.hasClass('is-edit'));
        formData.append("name", name);
        formData.append("date", date);
        formData.append("url", url);
        if (url != null) {
            formData.append("type", "url");
        } else {
            formData.append("type", "image");
        }
        formData.append("description", description);
        if (form.find("[name='image']")[0].files.length > 0)
            formData.append("image", form.find("[name='image']")[0].files[0]);
        if (form.find("[name='sound']")[0].files.length > 0) {
            formData.append("sound", form.find("[name='sound']")[0].files[0]);
            formData.append("type", "sound");
        }

        if (form.hasClass('is-edit')) {
            const $item = $('.portfolio-list-item.has-focus');
            formData.append('portfolio_id', $item.data('id'));

            $.ajax({
                method: 'POST',
                url: 'ajax/artists/portfolio.php',
                data: formData,
                cache: false,
                processData: false,
                contentType: false,
                success: function (response) {
                    form.removeClass('is-edit');
                    const $item = $('.portfolio-list-item.has-focus');
                    $item.removeClass('has-focus');
                    response = JSON.parse(response);
                    if (!response.error) {
                        const portfo = response.portfolio[0];
                        $item.find('.url').text(portfo.url);
                        $item.find('.date').text(portfo.date);
                        $item.find('.name').text(portfo.name);
                        $item.find('.description').text(portfo.description);
                        if (portfo.hasOwnProperty('image')) {
                            $item.find('.image').attr('src', portfo.image);
                        }
                        list.find('.alert').remove();
                        form.collapse('hide');
                    } else {
                        let message = "";
                        for (let i = 0; i < response.messages.length; i++) {
                            message += response.messages[i];
                            if (i !== response.messages.length - 1)
                                message += '\n';
                        }
                        list.find('.alert').remove();
                        list.prepend('<div class="alert alert-outline-danger">' + message + '</div>')
                    }
                },
                error: function (error) {
                    console.log("error : " + JSON.stringify(error));
                }
            });
        } else {
            $.ajax({
                method: "POST",
                url: 'ajax/artists/portfolio.php',
                data: formData,
                contentType: false,
                cache: false,
                processData: false,
                success: function (response) {
                    list.find('.alert').remove();
                    list.prepend(response);
                    if (list.find('.alert').length === 0) {
                        form.collapse('hide');
                        list.find('.list-empty').addClass('d-none');
                    }
                },
                error: function (error) {
                    console.log("error : " + JSON.stringify(error));
                }
            });
        }
    });

