    $('.studios_table').on('click', 'th:not(:last-of-type)', function () {
        const table = $(this).parents('table').eq(0);
        table.find('th.active').removeClass('active');
        $(this).addClass('active');
        let rows = table.find('tr:gt(0)').toArray().sort(comparer($(this).index()))
        this.asc = !this.asc
        if (!this.asc) {
            rows = rows.reverse()
        }
        for (let i = 0; i < rows.length; i++) {
            table.append(rows[i])
        }
    });

    function comparer(index) {
        return function (a, b) {
            const valA = getCellValue(a, index), valB = getCellValue(b, index);
            return !isNaN(valA) && !isNaN(valB) ? valA - valB : valA.toString().localeCompare(valB)
        }
    }

    function getCellValue(row, index) {
        switch (index) {
            case 0 :
                return parseInt($(row).find('.main__table-text--number a').text());
            case 1 :
                return $(row).find('input:checkbox').is(':checked') ? 1 : 0;
            case 2 :
            case 3 :
                return $(row).find('td').eq(index).children('div').text();
            case 4 :
                return parseInt($(row).find('.main__table-text--price').data('price'));
            case 5 :
                const classList = $(row).find('div').attr('class');
                return classList.indexOf('approval') >= 0 ? 2 : (classList.indexOf('pending') >= 0 ? 1 : 0);
            default :
                return index;
        }
    }

    $('.btn-add__allowed-hour').on('click', function () {
        const $this = $(this);
        const dateInput = $('[name="allowed_date"]');
        const hourInput = $('[name="allowed_hour"]');
        const list = $('.allowed_hours_list');
        if (dateInput.val() !== '' && hourInput.val() !== '') {
            // list.prepend("<span class='badge badge-secondary'>"+hourInput.find('option:selected').text()+", "+dateInput.val()+"</span>")
            list.prepend('<tr><td class="allowed_date">' + dateInput.val() + '</td><td class="allowed_hour">' + hourInput.find('option:selected').text() + '</td><td><a href="javascript:void(0)" class="delete">x</a></td></tr>')
            hourInput.find('option:selected').prop('selected', false);
            dateInput.val(null);
        }
    });

    $('.allowed_hours_list').on('click', 'a.delete', function (e) {
        e.preventDefault();
        $(this).closest('tr').remove();
    });

    $("input[name='first_name'],input[name='last_name'],input[name='email'],input[name='melli_code'],input[name='advise_price'],input[name='delivery_time'],textarea[name='experience'],textarea[name='order_description']")
        .on("blur", function () {
            if ($(this).val() !== $(this).data('current'))
                updateUserData($(this));
        });

    $('input[name="avatar"]').on('change', function () {
        let formData = new FormData();
        const _this = $(this);
        formData.append('avatar', $(this)[0].files[0]);
        $.ajax({
            method: 'POST',
            url: 'ajax/user_account.php',
            data: formData,
            cache: false,
            processData: false,
            contentType: false,
            success: function (response) {
                response = JSON.parse(response);
                if (!response.error) {
                    const overlayDiv = _this.parent().prev('.profile-box__avatar-overlay');
                    overlayDiv.parent().css({backgroundImage: 'url(' + response.avatar + ')'})
                    overlayDiv.attr('href', response.avatar);
                    overlayDiv.removeClass('d-none');
                    $('.profile-box__avatar-overlay').magnificPopup({type: 'image'});
                } else {
                    Snackbar.show({
                        text: response.hasOwnProperty('messages') ? response.messages[0] : 'مشکلی پیش آمد. مجددا امتحان کنید',
                        showAction: false,
                        pos: 'top-right',
                        duration: 3000
                    });
                }
            }, error: function (error) {
                console.log("err : " + error);
            }
        });
    });

    $(':checkbox[name="is_advisor"]').on('change', function (e) {
        e.preventDefault();
        $.ajax({
            method: 'POST',
            url: 'ajax/user_account.php',
            data: {
                name: 'accept_advisor'
            },
            success: function (response) {
                response = JSON.parse(response);
                if (!response.error) {
                    Snackbar.show({
                        text: response.being_advisor ? 'قابلیت مشاوره برای شما فعال شد. لطفا هزینه مشاوره را نیز وارد کنید' : 'قابلیت مشاوره غیر فعال شد',
                        showAction: false,
                        pos: 'top-right',
                        duration: 3000
                    });
                    $('.profile-menu__item-advisortimes').parent('li').toggleClass('d-none');
                }
            }, error: function (error) {
                console.log("error : " + error);
            }
        })
    })

    $('.btn__change-password').on('click', function (e) {
        e.preventDefault();
        const form = $(this).closest('form');
        $.ajax({
            method: 'POST',
            url: 'ajax/user_account.php',
            data: {
                old_password: form.find('[name="old_password"]').val(),
                password: form.find('[name="password"]').val(),
                password_confirmation: form.find('[name="password_confirmation"]').val()
            },
            success: function (response) {
                response = JSON.parse(response);
                form.prev(".input-error").remove();
                if (response.error) {
                    let errmsg = "";
                    for (let i = 0; i < response.messages.length; i++) {
                        errmsg += response.messages[i] + '<br>';
                    }
                    form.before("<span class='input-error'>" + errmsg + "</span>")
                } else {
                    form.find('input').val(null);
                    Snackbar.show({
                        text: response.messages[0],
                        showAction: false,
                        pos: 'top-right',
                        duration: 3000
                    });
                }
            }
        });
    });

    function updateUserData(input) {
        if (input !== null) {
            $.ajax({
                method: 'POST',
                url: 'ajax/user_account.php',
                data: {
                    name: input.attr('name'),
                    value: input.val(),
                },
                success: function (response) {
                    console.log("res : " + response);
                    response = JSON.parse(response);
                    if (input !== null) {
                        if (input.attr('name') === 'first_name' || input.attr('name') === 'last_name') {
                            $('.profile-box__username').text($('[name="first_name"]').val() + ' ' + $('[name="last_name"]').val());
                        }
                        input.siblings(".input-error").remove();
                        if (response.error) {
                            input.after("<span class='input-error'>" + response.messages[0] + "</span>")
                        } else {
                            input.data('current', input.val());
                            input.after('<svg xmlns="http://www.w3.org/2000/svg" class="validated" viewBox="0 0 24 24">\n' +
                                '                                <path d="M22.319,4.431,8.5,18.249a1,1,0,0,1-1.417,0L1.739,12.9a1,1,0,0,0-1.417,0h0a1,1,0,0,0,0,1.417l5.346,5.345a3.008,3.008,0,0,0,4.25,0L23.736,5.847a1,1,0,0,0,0-1.416h0A1,1,0,0,0,22.319,4.431Z"/>\n' +
                                '                            </svg>');
                            input.next('.validated').fadeIn("slow", function () {
                                input.next('.validated').fadeOut(5000);
                            });
                        }
                    }
                }
            });
        }
    }

